mapper.select:
        SELECT COUNT(1)
          FROM S_QUOT_REQ
         WHERE
               S_QUOT_REQ.ACC_CD = #{accCd}
           AND S_QUOT_REQ.CUST_CD = #{custCd}
		   AND S_QUOT_REQ.APL_NO = #{aplNo}
		   AND S_QUOT_REQ.APL_VERSION = #{aplVersion}




		   AND (S_QUOT_REQ.ANS_FLG = '1' OR S_QUOT_REQ.ANS_FLG = '2')

SELECT QUOT_REQ_SEQ as QUOT_REQ_SEQ,
       QUOT_SUP_LINE_NO as QUOT_SUP_LINE_NO
  FROM S_QUOT_REQ
 WHERE
       S_QUOT_REQ.ACC_CD = #{accCd}
   AND S_QUOT_REQ.CUST_CD = #{custCd}
   AND S_QUOT_REQ.APL_NO = #{aplNo}
   AND S_QUOT_REQ.APL_VERSION = #{aplVersion}
