mapper.select:
SELECT COUNT(ORDER_NO) FROM S_NEW_DLV_EST
WHERE ORDER_NO = (
SELECT ORDER_NO
FROM S_NEW_DLV_EST
WHERE S_NEW_DLV_EST.DLV_EST_SEQ = #{delivMngSeq}
)
AND S_NEW_DLV_EST.ACC_CD = #{utlUserCd}
AND S_NEW_DLV_EST.CUST_CD = #{buyerCompanyCd}
AND S_NEW_DLV_EST.DELIV_VERSION < #{delivDateKeyNoVsn}
AND DPO_FLG = '0'
