mapper.select:
SELECT 
       COUNT(1)
  FROM S_NEW_DLV_EST
 WHERE
       S_NEW_DLV_EST.ACC_CD = #{orderUserCd}
   AND S_NEW_DLV_EST.CUST_CD = #{buyerUserCd}


   AND S_NEW_DLV_EST.ORDER_NO = #{orderNo}
   AND S_NEW_DLV_EST.DPO_FLG = '0'
   AND S_NEW_DLV_EST.DELETE_FLG = '0'
   AND S_NEW_DLV_EST.AUTO_DELETE_FLG = '0'
