mapper.select:
SELECT
       S_DLV_EST_MNG.ACC_CD
  FROM S_DLV_EST_MNG
 WHERE
       S_DLV_EST_MNG.ACC_CD = #{orderUserCd}
   AND S_DLV_EST_MNG.CUST_CD = #{buyerUserCd}
   AND S_DLV_EST_MNG.ORDER_NO = #{orderNo}
   AND S_DLV_EST_MNG.DELIV_VERSION = #{delivVersion}
   AND S_DLV_EST_MNG.INQ_RESP_IND = #{inqRespInd}
   AND S_DLV_EST_MNG.LAST_UPD_DATE = #{lastUpdDate}
