mapper.select:
        SELECT
               S_NEW_RCV.ACC_CD AS "utlUserCd"
          FROM S_NEW_RCV
         WHERE
               S_NEW_RCV.ACC_CD = #{utlUserCd}
           AND S_NEW_RCV.CUST_CD = #{buyerUserCd}
		   AND S_NEW_RCV.ORDER_NO = #{orderNo}
		   AND S_NEW_RCV.DELIV_KEY_NO = #{delivKeyNo}
		   AND S_NEW_RCV.DELIV_VERSION = #{delivKeyNoVersion}
		   AND S_NEW_RCV.LAST_UPD_DATE = #{lastUpdDate}
